export const createTicket = (paypalId, seat) => {
  const movie_id = localStorage.getItem('movie');
  const movie = fetchMovie(movie_id);
  fetch('http://127.0.0.1:5000/api/tickets', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      Authorization: `Bearer ${localStorage.getItem('access_token')}`
    },
    body: JSON.stringify({
      id: 'CIN32',
      movie: movie.title,
      cinema: 'Cinemate Cineplex',
      booking_id: paypalId,
      price: 10.0,
      seat_number: seat
    })
  })
    .then(response => {
      console.log(response.status);
      return response.json();
    })
    .then(jsondata => console.log(jsondata));
};

const fetchMovie = (id) => {
  let movie = {};
	   fetch(`http://127.0.0.1:5000/api/movies/${id}`)
	       .then(response => response.json()).then(data => {
		       movie = data;
		       console.log(data);
	       })
    .catch(err => console.error(err));
  return movie;
};
