import React, { useState, useEffect } from 'react'
import '../static/movies.css'

export default function HomePage() {
    const [movies, setMovie] = useState([])


    const fetchMovies = async () => {
    try {
        const response = fetch("http://localhost:5000/api/movies");
        if (!response.ok) {
        throw new Error("Failed to fetch movies");
        }
        const data = response.json();
        setMovie(data);
    } catch (error) {
        console.log(error);
    }
};

  return (
    <>
        <header>
        <input type="text" name="movies" id="movies" placeholder="Search movies by genre"/>
        <button onClick={fetchMovies}>Search</button>
        </header>
        <hr/>
        <div className="container">
        {movies.map((movie) => (
            <div className="movies">
            <iframe width="150" height="200" src={movie.trailer_url} frameborder="0" allowfullscreen></iframe>

            <img src={movie.poster_url}/>
                <a href={movie.trailer_url}>{movie.title}</a>
            </div>
        ))}
        </div>
    </>
  )
}
