export const createTicket = (paypalId, seat) => {
  let movie_id = localStorage.getItem('movie');
  let movie = fetchMovie(movie_id);
  fetch('http://127.0.0.1:5000/api/tickets', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      Authorization: `Bearer ${localStorage.getItem('access_token')}`
    },
    body: JSON.stringify({
      id: 'CIN32',
      movie: movie.title,
      cinema: 'Cinemate Cineplex',
      booking_id: paypalId,
      price: 10.0,
      seat_number: seat
    })
  })
    .then(response => {
      console.log(response.status);
      return response.json();
    })
    .then(jsondata => console.log(jsondata));
};

const fetchMovie = async (id) => {
    try {
	    const response = await fetch(`http://127.0.0.1:5000/api/movies/${id}`);
	    const data = await response.json();
	    return data;
    } catch (err) {
	    console.error(err);
    }
  };
