import React, { useState, useEffect } from 'react'
import '../static/movies.css'

export default function HomePage() {
    const [movies, setMovies] = useState([]);

    const fetchMovies = async () => {
      const response = await fetch('http://127.0.0.1:5000/api/movies')
      const data = await response.json()
        console.log(data)
        setMovies(data);
    }
    useEffect(() => {
        fetchMovies
      }
    , []);

    return (
      <>
        <header>
          <input type="text" name="movies" id="movies" placeholder="Search movies by genre" />
          <button>Search</button>
        </header>
        <hr />
        <div className="movies-container">
          {movies.map((movie, index) => (
            <div className="movies" id={index}>
              <iframe width="150" height="200" src={movie.trailer_url} frameBorder="0" allowFullScreen></iframe>
              <img src={movie.poster_url} alt={movie.title} />
              <a href={movie.trailer_url}>{movie.title}</a>
            </div>
          ))}
        </div>
      </>
    );
  }
