import React, { useState, useEffect } from 'react';
import {Link, useLocation} from 'react-router-dom';
import '../static/tickets.css';
import Footer from './Footer';
export default function Tickets () {
  const ticketData = [
    {
      id: 'CIN1',
      movie: 'The Lion King',
      cinema: 'AMC Theatres',
      //date: '2023-06-12',
      //time: '18:00',
      booking_id: '456',
      price: 10,
      seat_number: 'B2'
    },
    {
      id: 'CIN32',
      movie: 'The Lion King',
      cinema: 'AMC Theatres',
      //date: '2023-06-12',
      //time: '18:00',
      booking_id: '123',
      price: 10,
      seat_number: 'B2'
    }
  ];
  const putData = async() => {
      const response = await fetch('http://127.0.0.1:5000/api/tickets', {
	          //header
	      });
      const data = await response.json()
  }
  return (
    <>
    <header>
	 <div className="logo"><i className="fas fa-user-circle user-icon"/>Hi, user</div>
	 <nav>
	  <ul>
          <li><Link to='/'>home</Link></li>
          <li>signout</li>
	  </ul>
        </nav>
    </header>
    <div className='tickets-container'>
      <div className='tickets'>
        <h2>My Tickets<i className='fas fa-ticket-alt ticket-icon' /></h2>
        <p>View all tickets you have booked</p>
      </div>
      <div className='table-wrapper'>
        <table className='ticket-table'>
          <thead>
            <tr>
              <th>Ticket ID</th>
              <th>Date</th>
              <th>Time</th>
              <th>Payment</th>
              <th>Seat Number</th>
	      <th>Ticket</th>
              {/* Add additional table headers as needed */}
            </tr>
          </thead>
          <tbody>
            {ticketData.map((ticket) => (
              <tr key={ticket.id}>
                <td>{ticket.id}</td>
                <td>{ticket.date}</td>
                <td>{ticket.time}</td>
                <td>{ticket.payment}</td>
                <td>{ticket.seatNumber}</td>
		<td><button className='ticket-button' onClick={() => {
			localStorage.setItem('ticket', ticket.id)
		}}>View</button></td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
     </div> 
    <Footer />
   </>
  );
}
