export const createTicket = (paypalId) => {
  // Perform the ticket creation logic here
  fetch('http://127.0.0.1:5000/api/tickets', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      Authorization: `Bearer ${localStorage.getItem('access_token')}`
    },
    body: JSON.stringify({
      id: 'CIN32',
      movie: 'The Lion King',
      cinema: 'AMC Theatres',
      booking_id: paypalId,
      price: 10.0,
      seat_number: `${localStorage.getItem('array').split(',')[0]}`
    })
  })
    .then(response => {
      console.log(response.status);
      return response.json();
    })
    .then(jsondata => console.log(jsondata));
};
