import React, { useState} from 'react'
import '../static/movies.css'

export default function HomePage() {
    const [movies, setMovie] = useState([])

    fetch("http://127.0.0.1:5000/api/movies").then(response => response.json()).then(data => {
        setMovie(data)
    }).catch(err => console.error(err))

  return (
    <>
        <header>
        <input type="text" name="movies" id="movies" placeholder="Search movies by genre"/>
        <button>Search</button>
        </header>
        <hr/>
        <div class="container">
        {movies.map(movie => {
            <div class="movies">
            <iframe width="150" height="200" src={movie.trailer_url} frameborder="0" allowfullscreen></iframe>

            <img src={movie.poster_url}/>
                <a href={movie.trailer_url}>{movie.title}</a>
            </div>
        })}
        </div>
    </>
  )
}
