import React, {useState, useEffect} from 'react';
import QRCode from 'qrcode.react';
import '../static/ticket.css';
import logo from '../logo.png';
import Footer from './Footer';

export default function Ticket () {
  const [ticket, setTicket] = useState({});
  const [date, setDate] = useState('');
  const getData = (ticket_id) => {
    fetch(`http://127.0.0.1:5000/api/tickets/${ticket_id}`, {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${localStorage.getItem('access_token')}`
      }
    }).then(response  => response.json()).then(data => {
	    setTicket(data)
	    setDate(new Date(data.created_at));
	    console.log(data)
    });
  }
  useEffect(() => {
      const id = localStorage.getItem('ticket')
      getData(id);
  }, [])
  return (
    <>
    <div className='ticket'>
      <div className='ticket-header'>
        <img src={logo} />
        <p style={{textTransform:'uppercase'}}><strong>date generated: </strong>{date.toISOString().split('T')[0]}</p>
      </div>
      <div className='ticket-info'>
        <div className='details'>
          <div className='distributions'>
            <span>Ticket ID:</span>
            <p>{ticket.id}</p>
          </div>
          <div className='distributions'>
            <span>Movie:</span>
            <p>{ticket.movie}</p>
          </div>
          <div className='distributions'>
            <span>Cinema:</span>
            <p>{ticket.cinema}</p>
          </div>
	  <div className='distributions'>
            <span>Streaming:</span>
	  {/*<p>{new Date(ticket.created_at).toISOString().split('T')[0]}</p>*/}
          </div>
          <div className='distributions'>
            <span>Time:</span>
	  {/*<p>{new Date(ticket.created_at).toISOString().split('T')[1].split('.')[0]}</p>*/}
          </div>
          <div className='distributions'>
            <span>Price:</span>
            <p>{ticket.price}</p>
          </div>
          <div className='distributions'>
            <span>Seat Number:</span>
            <p>{ticket.seat_number}</p>
          </div>
        </div>
        <div className='qrcode'>
          <QRCode value={JSON.stringify(ticket)} size={128} />
        </div>
      </div>
    </div>
    <Footer/>
    </>
  );
}
