import React, { useEffect, useState } from 'react';
import '../static/seatmap.css';
export default function SeatMap () {
  const [count, setCount] = useState(0);
  const [price, setPrice] = useState(0)
  /*const click = () => {
    const seats = document.querySelectorAll('.row .seat:not(.occupied)');
    seats.forEach(seat => {
	    seat.addEventListener('click', (e) => {
        if (seat.classList.contains('selected')) {
          seat.classList.remove('selected');
          setCount(prev => prev - 1);

        } else {
		    seat.classList.add('selected');
		    setCount(prev => prev + 1);
		    setPrice(count * 10);
	    }
		    setPrice(count * 10);
      });
    });
  };
  useEffect(() => {
	  click()
  }, [])*/
  const handleSeatClick = (e) => {
    const clickedSeat = e.target;

    if (clickedSeat.classList.contains('selected')) {
      clickedSeat.classList.remove('selected');
      localStorage.setItem(clickedSeat.id, 'clicked');
      setCount(prev => prev - 1);
    } else {
      clickedSeat.classList.add('selected');
      setCount(prev => prev + 1);
    }
  };

  useEffect(() => {
    const seats = document.querySelectorAll('.row .seat:not(.occupied)');
    seats.forEach(seat => {
      seat.addEventListener('click', handleSeatClick);
    });

    /*return () => {
      seats.forEach(seat => {
        seat.removeEventListener('click', handleSeatClick);
      });
    };*/
  }, []);

  useEffect(() => {
    setPrice(count * 10);
  }, [count]);
  return (
    <div className='body'>
      <div className='seatmap-container'>
        <label>Pick a Movie:</label>
        <select id='pick-movie'>
          <option value='10' />
          <option value='12' />
          <option value='8' />
          <option value='9' />
        </select>
      </div>
      <ul className='showcase'>
        <li>
          <div className='seat' />
          <small>N/A</small>
        </li>
        <li>
          <div className='seat selected' />
          <small>Selected</small>
        </li>
        <li>
          <div className='seat occupied' />
          <small>Occupied</small>
        </li>
      </ul>
      <div className='seats-container'>
        <div className='screen' />
        <div className='row'>
          <div className='seat occupied' />
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
        </div>
        <div className='row'>
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
          <div className='seat occupied' />
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
        </div>
        <div className='row'>
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
        </div>
        <div className='row'>
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
        </div>
        <div className='row'>
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
          <div className='seat' />
